}, {
  title: '缩略图',
  dataIndex: 'img',
  width: '20%',
  key: 'img',
  align: 'center'
}, {
  title: '操作',
  width: '30%',
  key: 'action',
  align: 'center',
  scopedSlots: {
    customRender: 'action'
  }
}];
export default {
  data() {
    return {
      pagination: {
        pageSizeOptions: ['5', '10', '20'],
        defaultCurrent: 1,
        defaultPageSize: 5,
        total: 0,
        showSizeChanger: true,
        showTotal: total => `共${total}条`
      },
      artList: [],
      columns,
      queryParam: {
        artname: '',
        pagesize: 5,
        pagenum: 1
      },
      newart: {
        artname: '',
        password: '',
        checkpass: '',
        role: 2
      },
      artinfo: {
        id: 0,
        artname: '',
        role: 2
      },
      addArtRules: {
        artname: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }, {
          min: 4,
          max: 12,
          message: '用户名应当在4到12之间',
          trigger: 'blur'
        }],
        password: [{
          validator: (rule, value, callback) => {
            if (value === '') {
              callback(new Error('请输入密码'));
            }
            if (value.length < 6 || value.length > 20) {
              callback(new Error('密码应当在6到20为之间'));
            } else {
              callback();
            }
          },
          trigger: 'blur'
        }],
        checkpass: [{
          validator: (rule, value, callback) => {
            if (value === '') {
              callback(new Error('请输入密码'));
            }
            if (value !== this.newart.password) {
              callback(new Error('密码不一致，请重新输入'));
            } else {
              callback();
            }
          },
          trigger: 'blur'
        }]
      },
      editArtRules: {
        artname: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }, {
          min: 4,
          max: 12,
          message: '用户名应当在4到12之间',
          trigger: 'blur'
        }]
      },
      addArtVisible: false,
      editArtVisible: false,
      visible: false
    };
  },
  created() {
    this.getArtList();
  },
  methods: {
    async getArtList() {
      const {
        data: res
      } = await this.$http.get('arts', {
        params: {
          artname: this.queryParam.artname,
          pagesize: this.queryParam.pagesize,
          pagenum: this.queryParam.pagenum
        }
      });
      if (res.status !== 200) return this.$message.error(res.message);
      this.artList = res.data;
      this.pagination.total = res.total;
    },
    // 修改分页时触发
    handleTableChange(pagination, filters, sorter) {
      const pager = {
        ...this.pagination
      };
      pager.current = pagination.current;
      pager.pageSize = pagination.pageSize;
      this.queryParam.pagesize = pagination.pageSize;
      this.queryParam.pagenum = pagination.current;
      if (pagination.pageSize !== this.pagination.pageSize) {
        this.queryParam.pagenum = 1;
        pager.current = 1;
      }
      this.pagination = pager;
      this.getArtList();
    },
    // 删除用户
    deleteArt(id) {
      this.$confirm({
        title: '确定删除该用户吗？',
        content: '一旦删除，无法恢复',
        onOk: async () => {
          const {
            data: res
          } = await this.$http.delete(`art/${id}`);
          if (res.status !== 200) return this.$message.error(res.message);
          this.$message.success('删除成功');
          this.getArtList();
        },
        onCancel: async () => {
          this.$message.info('已取消删除');
        }
      });
    },
    // 新增用户
    addArtOk() {
      this.$refs.addArtRef.validate(async valid => {
        if (!valid) return this.$message.error('参数不符合要求，请重新输入');
        const {
          data: res
        } = await this.$http.post('/art/add', {
          artname: this.newart.artname,
          password: this.newart.password,
          role: this.newart.role
        });
        if (res.status !== 200) return this.$message.error(res.message);
        this.addArtVisible = false;
        this.$message.info('添加用户成功');
        this.getArtList();
      });
    },
    addArtCancel() {
      this.$refs.addArtRef.resetFields();
      this.addArtVisible = false;
      this.$message.info('新增用户已取消');
    },
    newRoleChange(checked) {
      this.newart.role = checked ? 1 : 2;
    },
    // 编辑用户
    async editArt(id) {
      this.editArtVisible = true;
      const {
        data: res
      } = await this.$http.get(`/art/${id}`);
      if (res.status !== 200) this.$message.error(res.message);
      this.artinfo.artname = res.data.artname;
      this.artinfo.role = res.data.role;
      this.artinfo.id = id;
    },
    editArtOk() {
      this.$refs.editArtRef.validate(async valid => {
        if (!valid) return this.$message.error('参数不符合要求，请重新输入');
        const {
          data: res
        } = await this.$http.put(`art/${this.artinfo.id}`, {
          artname: this.artinfo.artname,
          role: this.artinfo.role
        });
        if (res.status !== 200) return this.$message.error(res.message);
        this.editArtVisible = false;
        this.$message.info('编辑用户信息成功');
        this.getArtList();
      });
    },
    editRoleChange(value) {
      this.artinfo.role = Number(value);
    },
    editArtCancel() {
      this.editArtVisible = false;
      this.$refs.editArtRef.resetFields();
      this.$message.info('编辑已取消');
    }
  }
};
